INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (12, '十二峰送青排闼，自天宝以飞来', '五百年逃墨归儒，跨开元之顶上', '', '', '2025-03-18 06:06:39');
