INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (28, '光依东壁图书府', '心在西湖山水间', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (29, '官居东壁图书府', '家住西湖山水间', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (30, '始余起兵于濠上，先崇捧日之心', '逮兹定鼎于江南，遂作擎天之柱', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (31, '破虏平蛮，功贯古今人第一', '出将入相，才兼文武世无双', '', '', '2025-03-18 06:06:39');
