INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (33, '双手劈开生死路', '一刀割断是非根', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (34, '洛水元龟初献瑞，阴数九，阳数九，九九八十一数，数通乎道，道合元始天尊，一诚有感', '岐山丹凤两呈祥，雄鸣六，雌鸣六，六六三十六声，声闻于天，天生嘉靖皇帝，万寿无疆', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (35, '揲灵蓍之草以成文，天数五，地数五，五五二十五数，数生于道，道合元始天尊，尊无二上', '截嶰竹之筒以协律，阳声六，阴声六，六六三十六声，声闻于天，天生嘉靖皇帝，帝统万年', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (36, '上相太师，一德辅三朝，功高日月', '状元榜眼，二难登两第，学冠天人', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (37, '庭训尚存，老去敢忘佩服', '国恩未报，归来犹抱惭惶', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (38, '山中宰相无官府', '天上神仙有子孙', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (39, '避贤初罢相', '乐圣且衔杯', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (40, '但将药裹供衰病', '未有涓浚答圣朝', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (41, '日月并明，万国仰大明天子', '丘山为岳，四方颂太岳相公', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (42, '父冢宰，子冢宰，秉一代之铨衡', '兄司徒，弟司徒，总两京之会计', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (43, '四万八千丈山中仙客', '三百六十重滩上闲官', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (44, '海无波涛，海瑞之工不浅', '林有梁栋，林润之泽居多', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (45, '貌如卢杞心尤险', '学比荆公性更偏', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (46, '忠可格天，正气垂之万世', '功昭捧日，休光播于百年', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (47, '无子无孙，尽是他人之物', '有花有酒，聊为卒岁之欢', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (48, '岂有文章惊海内', '漫劳车马驻江干', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (49, '天为补贫偏与健', '人因见懒误称高', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (50, '松风高士供', '兰梦美人圆', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (51, '门前白水流将去', '屋里青山跳出来', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (52, '春梦惯迷人，九环仙骨，误著了一品朝衣，任鸡鸣紫陌，马踏红尘，军门向那头跳出', '空山曾约伴，六诏杯茶，犹记得七闽片语，看剑影横天，笛声吹海，先生从何处飞来', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (53, '门多将相文中子', '身系安危郭令公', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (54, '夏至酉逢三伏热', '重阳戊遇一冬晴', '', '', '2025-03-18 06:06:39');
