INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (60, '雨中春树万人', '云里帝城双凤', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (61, '一池金玉如如化', '满眼青黄色色真', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (62, '未必玄关别名教', '须知书户孕江山', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (63, '炼石补星辰，两月兴工当万历，缵禹之绪', '凿山振河海，千年遗迹在三江，于汤有光', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (64, '浩气塞两间，万古纲常永赖', '威灵宣八表，千秋带砺全凭', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (65, '汉封侯，晋封王，有明封帝，圣天子可谓厚矣', '内有奸，外有敌，中原有贼，大将军何以待之', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (66, '万事不如杯在手', '一年几见月当头', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (67, '但愿桑麻成乐土', '不妨诗酒上邻霄', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (68, '读儒书不奉佛教', '遵母命权作道场', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (69, '近圣人之居，教亦多术矣', '守先生之道，文不在兹乎', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (70, '淮蔡之功茂矣，抑又过之，大难削平，重奠东南半壁', '琴鹤之操凛然，于今为烈，名贤继起，恰符五百余年', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (71, '石压笋斜出', '岩垂花倒开', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (72, '名满天下，不曾出户一步', '言满天下，不曾出口一字', '', '', '2025-03-18 06:06:39');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (73, '欲知世味须尝胆', '不识人情只看花', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (74, '非关因果方为善', '不计科名始读书', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (76, '谦卦六爻皆吉', '恕字终身可行', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (77, '敏则有功公则说', '淡而不厌简而文', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (78, '谈性命则先贤之说已多，何似求之践履', '学考订则就衰之年无及，不如返诸身心', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (80, '宽一分民受一分，见祜鬼神', '要一文不值一文，难欺吏卒', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (81, '不畏官司千状纸', '只怕乡民三寸刀', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (82, '直者无庸我力，枉者我无庸力，何敢贪天之功', '恩则以奸为贤，怨则以贤为奸，岂能逃鬼之责', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (83, '青天下鉴此心，敢不光明正直', '赤子来游吾腹，愿言岂弟慈祥', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (84, '真理学从五伦做起', '大文章自六经分来', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (85, '学古之志未衰，每日必拥书早起', '干世之心已绝，无夕不饮酒高歌', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (86, '于三纲五常内，力尽一分，就算一分真事业', '向六经四子中，尚论千古，才识千古大文章', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (87, '反已有真修，须留神检到心身界上', '加工无别法，务著力打开义利关头', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (88, '人重官非官重人', '德胜才毋才胜德', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (89, '欺人如欺天，毋自欺也', '负民即负国，何忍负之', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (90, '无求便是安心法', '不饱真为却病方', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (91, '要除烦恼须成佛', '各有来因莫羡人', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (92, '非名山不留仙住', '是真佛只说家常', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (93, '君不可负，只是心难负，负心者不容于尧舜', '天不可欺，谁言人易欺，欺人者如见其肺肝', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (94, '但愿民安若堵', '何妨署冷如冰', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (95, '亏他人便亏自己，须记朝齑暮盐，我亦寒士', '要公道还要虚心，试看畹兰亩蕙，楚故有材', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (96, '才要真爱，名要略爱，总之己要自爱', '天不可欺，君不敢欺，实于心不忍欺', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (97, '畏简书并畏人言，常以无欺盟夙夜', '正文风先正士习，惟将有耻勖胶庠', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (98, '尔无文字当安命', '我有儿孙要读书', '', '', '2025-03-18 06:10:00');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (99, '眼前百姓即儿孙，莫言百姓可欺，当留下儿孙地步', '堂上一官称父母，漫说一官易做，还尽些父母恩情', '', '', '2025-03-18 06:10:00');
