INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (117, '我也曾为冤枉痛入心来，敢糊涂忘了当日', '汝不必逞机谋争个胜去，看终久害著自家', '', '', '2025-03-18 06:10:00');
