INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (121, '庭余嘉荫，室有藏书，天下事随处而安，即此是雕梁画栋', '卜得芳邻，居成美境，田舍翁问心已足，漫言应列鼎鸣钟', '', '', '2025-03-18 06:10:00');
