INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (531, '东方先生善谐谑', '南极老人应寿昌', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (532, '国门旧价千金重', '乡社新图九老尊', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (533, '会翻李委南飞曲', '曾领朱明大洞天', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (534, '鼓琴自擅寰中手', '飞舄来看海上山', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (535, '履舄殿庭千叟上', '台光角亢两星间', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (536, '家传玉节仍分陕', '天赐琼筵预杖期', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (537, '藏山事业三干牍', '住世神明五百年', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (538, '旌麾畿甸尊宗衮', '杖履耆英寿国桢', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (539, '玉节承天南斗静', '锦衣行昼使星明', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (540, '龙门世泽华跗盛', '鲸海威名草木知', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (542, '子未必肖，孙未必贤，屡忝科名，只为老年娱晚景', '夫岂能刚，妻岂能顺，重谐花烛，幸邀天眷锡遐龄', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (543, '务观万篇，半皆归里作', '启期三乐，同是达生言', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (544, '彩云见处，先后六十年中，三世建牙，宜何如效涓埃以肯堂构', '湛露承来，纵横八千里外，百蛮稽首，所愿与兴礼让而广农桑', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (545, '有客临舟怀帝子', '何人下榻学陈公', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (546, '能文翰林，能言御史，能任封疆赞翊圣化，有真经济才，公论出朝士口', '为国筹运，为民兴利，为我乡里表彰贤达，得大光明寿，妙法现宰官身', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (547, '八州都督，五柳先生，经济文章，千古心传家学远', '六甲初周，一阳来复，富贵寿考，百年身受国恩长', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (548, '海涵地负之才，文章博辨，议论雄伟', '春温秋肃之政，动用县解，静专直方', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (549, '再世宫衔，太保少保', '两朝宸眷，司徒司空', '', '', '2025-03-18 06:10:42');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (550, '气备四时，与天地日月鬼神合其德', '教垂万世，继尧舜禹汤文武作之师', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (551, '尊王言必称尧舜', '忧世心同切孔颜', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (552, '治统溯钦承，法戒兼资，洵哉古可为监', '政经崇秩祀，实枚式焕，穆矣其神孔安', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (553, '蒙难观爻，石径蒺藜皆卦象', '拘幽作操，云田柞棫亦琴材', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (554, '战国风趋下', '斯文日再中', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (555, '孔门功冠三千士', '周室生当五百年', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (556, '述格致诚正修齐治平之传，万世咸承厥训', '超德行言语政事文学而外，一人独得其宗', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (557, '衍一人忠恕之心传，学惟省贯', '开万世治平之事业，道极明新', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (558, '允矣圣人之徒，闻善则行，闻过则喜', '大哉夫子之勇，见危必拯，见义必为', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (559, '性道在文章，深造自得', '廉平称治绩，遗爱无穷', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (560, '馨香分郭外之田，夕膳晨馐，讵敢作拾尘野祭', '展拜守家中之训，左昭右穆，何须繙争坐名书', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (561, '薄稼圃而不为，宜善会先贤之意', '敬鬼神而仍远，当恪尊乃祖所闻', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (562, '旧官宁改汉', '遗恨失吞吴', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (563, '汉家宫阙来天上', '武帝旌旗在眼中', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (564, '吴宫花草埋幽径', '魏国山河半夕阳', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (565, '三分割据纡筹策', '万国衣冠拜冕旒', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (566, '先武穆而神，大汉千古，大宋千古', '后文宣而圣，山东一人，山西一人', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (567, '赫厥声，濯厥灵，无师保如临父母', '天所覆，地所载，有血气莫不尊亲', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (568, '圣以武成名，刚毅近仁，于清任时和中更增一席', '学于古有获，春秋卒业，在诗书易礼外别有专经', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (569, '圣至于神，荐馨历千载而遥，如日月行天，江河行地', '湖开自汉，崇祀值两峰相对，有武穆在北，忠肃在南', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (570, '德必有邻，把臂呼岳家父子', '忠能择主，鼎足定汉室君臣', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (571, '兵法读春秋，必有文事', '官箴严月旦，无作神羞', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (572, '心之光明犹火也', '神而变化其龙乎', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (573, '史官拟议曰矜，误矣，视吴魏诸人，原如无物', '后世尊崇为帝，敢乎，论春秋大义，还是汉臣', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (574, '惠陵烟雨，涿郡风雷，在昔埙篪兴一旅', '魏国山河，吴宫花草，于今蛮触笑三分', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (575, '数定三分，扶汉室削吴吞魏，辛苦备尝，未了平生事业', '志存一统，佐熙朝伏寇降魔，威灵丕振，只完当日精忠', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (576, '识者观时，当西蜀未收，昭烈尚无尺土，操虽汉贼，犹是朝臣，至一十八骑走华容，势方穷促，而慨释非徒报德，只缘急国计而缓奸雄，千古有谁共白', '君子喻义，恨东吴割据，刘氏已失偏隅，权即人豪，讵应抗主，以八十一州称敌国，罪实难逃，而拒婚岂曰骄矜，明示绝强援以尊王室，寸心只在自知', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (577, '三教尽皈依，正直聪明，心似日悬天上', '九州隆享祀，英灵昭格，神如水在地中', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (578, '不爱财，不爱酒，不爱妇人，是个老头陀，只因眉宇间带两字英雄，担搁了五百年入山正果', '又要忠，又要孝，又要风流，好场大冤孽，若非胞胎里有三分痴钝，险些做十八滩顺水行舟', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (579, '此吴地也，不为孙郎立庙', '今帝号矣，何须曹氏封侯', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (580, '拜斯人，便思学斯人，莫混帐磕了头去', '入此山，须要出此山，当仔细扪著心来', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (581, '生何氏，没何年，盖弗可考矣', '夫尽忠，子尽孝，可不谓贤乎', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (582, '雄猛让一人，武善提戈文握管', '精英传万世，唐曾显姓宋留名', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (583, '宇宙大文章，源从孝友', '古今名将相，气作星辰', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (584, '天惟阴骘下民，止于仁，止于敬', '帝乃诞敷文德，作之君，作之师', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (585, '积忠孝以成神，典桂籍科名，予夺后先，十五国文章司命', '举阴骘而垂训，鉴槐区德行，权衡富贵，亿万年造化枢机', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (586, '天开参井文章府', '星焕山河孝友师', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (587, '帝以会昌，神以建福', '下有风雅，上有日星', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (588, '违亲不孝，背君不忠，敢辞瘴雨蛮烟，采药当年心最苦', '传季而王，偕仲而霸，岂意吴头楚尾，瓣香到处德维新', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (589, '勾吴分土惟三，端委垂型，梅里肇基名最古', '迁史世家第一，云仍衍绪，华陂崇祀惠无疆', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (590, '逞披发仗剑威风，仙佛焉耳矣', '有降龙伏虎手段，龟蛇云乎哉', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (591, '教孝教忠，何殊十七世士夫，显示化身扶正道', '为溪为谷，直把五千言文字，参同妙契指迷津', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (592, '遇有缘人，不枉我望穿眼孔', '得无上道，只要汝立定脚跟', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (593, '彩笔昔曾干气象', '文昌新入有光辉', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (594, '笔足代耕，不厌兼金归掌握', '文能行远，何妨只履上云霄', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (595, '法云广荫无遮会', '慧日高悬有相天', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (596, '泡影乾坤，妆成宝相', '色香世界，幻出空花', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (597, '莲字岧峣，去天尺五临韦曲', '芦塘淼漫，在水中央认补陀', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (598, '真实不虚，大慈悲度一切苦厄', '意识无界，空色相现五蕴光明', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (599, '菩提今菩提，具大神通，忽现千般手眼', '自在观自在，是真佛力，总由一念慈悲', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (600, '即色即空，现慈悲相，吾无隐尔', '是万是一，具手眼人，自能辨之', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (601, '音亦可观，方信聪明无二用', '佛何称士，须知儒释有同源', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (602, '白衣仙人，瓶中水杨柳', '朱芾男子，天上石麒麟', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (603, '我具一片婆心，抱个孩儿送汝', '你做百般好事，留些阴骘与他', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (604, '可托六尺之孤，可寄百里之命，君子人与，君子人也', '隐居以求其志，行义以达其道，吾闻其语，吾见其人', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (605, '自任以天下之重如此', '是知其不可而为之与', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (606, '伊吕允堪俦，若定指挥，岂仅三分兴霸业', '魏吴偏并峙，永怀匡复，犹余两表见臣心', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (607, '梁父吟成高士志', '出师表见老臣心', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (608, '成大事以小心，一生谨慎', '仰流风于遗像，万古清高', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (609, '起八代衰，自昔文章尊北斗', '兴四门学，即今俎豆重东胶', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (610, '进学解成，闲官一席曾三仕', '起衰力任，巨制千秋本六经', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (611, '与水不争能，力尽八年惟注海', '升堂思肯构，目穷千里更登台', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (612, '洪水想当年，幸怪锁洪湖，十万户饭美鱼香，如依夏屋', '清时思俭德，祝神来清浦，千百载泳勤沐泽，共乐春台', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (613, '本来上界神仙，喜功德在民，辉联俎豆', '同是盛朝臣子，愿灵明诱我，绩奏江湖', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (614, '巫支祈井底深潜，澜恬洪泽', '阿罗汉波间重出，福佑清淮', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (615, '示梦记江头，曾许我一帆风顺', '安澜遍河上，愿长此大树春荣', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (616, '圣世不鸣条，默佑湖河占利涉', '神功常应律，潜调寒燠叶休征', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (617, '泄造化之机缄，万世文章开易象', '规山川之形胜，千秋陵寝奠淮阳', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (618, '后天地而生，朱圉犹堪寻圣迹', '立帝王之极，白云常此护灵墟', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (619, '君德难回，当此众叛亲离，若但如微子去，箕子奴，无以激亿万人忠贞之气', '臣心不死，即兹魂飞血溅，犹将以周日兴，殷日丧，上诉诸六七王陟降之灵', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (620, '万井烟浓，人间正熟黄粱饭', '四山云起，天上应开白奈花', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (621, '睡至二三更时，凡功名都成幻境', '想到一百年后，无少长俱是古人', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (622, '杯前三尺青蛇，仙会恍游蓬岛路', '笛外一声黄鹤，我来犹记洞庭秋', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (623, '卅二色神仙宝光，也似佛，也似儒，出世还入世', '五千言道德真嗣，亦称师，亦称祖，可名非常名', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (624, '兄让弟，弟让兄，父命天伦千古重', '圣称贤，贤称圣，顽廉懦立百世师', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (625, '大节凛东藩，四百载至今如昨', '崇祠留北渚，万千劫虽死犹生', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (626, '养性承天，仙教源流通圣教', '修真得地，济南风景赛终南', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (627, '山高则配天，阳鲁阴齐资化育', '坤厚故载物，西河东海仰生成', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (628, '宫中下见南山尽', '城上平临北斗悬', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (629, '云行雨施，不崇朝而遍天下', '理大物博，祖阳气之发东方', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (630, '天下太平无一事', '山中高卧有千秋', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (631, '今尚祀虞，东汉已无高后庙', '斯真霸越，西施羞上范家船', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (632, '事父未能，入庙倾诚皆末节', '悦亲有道，见吾不拜也无妨', '', '', '2025-03-18 06:10:58');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (633, '由孔孟而来，二千年卫道传经，独振斯文统绪', '当光宁之世，五十日格非陈善，允宜此地烝尝', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (634, '大哉夫子之功，百世权衡，六经羽翼', '远矣斯文之统，周程私淑，孔孟闻知', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (635, '亲不负楚，疏不负梁，爱国忠君真气节', '骚可为经，策可为史，经天行地大文章', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (636, '何处招魂，香草还生三户地', '当年呵壁，湘流应识九歌心', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (637, '一门父子三词客', '千古文章四大家', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (638, '是父生是子，家学一门，自昔声名弥宇宙', '难兄更难弟，象贤两世，至今俎豆重乡邦', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (639, '江山故宅空文藻', '父子高名重古今', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (640, '洗菜莫教流去叶', '见桃犹记旧曾花', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (641, '中国有圣人，是祖是师，咄咄西来东土', '名山藏帝子，亦仙亦佛，元元北镇南天', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (642, '顾曲有闲情，不碍破曹真事业', '饮醇原雅量，偏嫌生亮并英雄', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (643, '大帝君臣同骨肉', '小乔夫婿是英雄', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (644, '九伐竟无成，心师武侯，能继祁山六出志', '三分不可恃，计诛邓艾，已复阴平一败仇', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (645, '田鼓祝桑麻，丹荔黄蕉隆肸蚃', '云旗回岛屿，珠宫贝阙奠灵长', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (646, '神德庇三农，统天田以乾象', '思膏流百粤，兴云雨于雩坛', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (647, '嘉澍庆知时，仰神赞天功，灵嘘元气', '大田歌既渥，看泽周南海，福庇东瀛', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (648, '国士无双双国士', '忠臣不二二忠臣', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (649, '铜柱镇鸢飞，顾盼生风，意气真能吞浪泊', '金门留马式，男儿报国，姓名何必与云台', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (650, '才与福难兼，贾傅以来，文字潮儋同万里', '地因人始重，河东而外，江山永柳各千秋', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (651, '双柏仰清标，长忆养人如树', '一池寻故迹，同欣凿井得泉', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (652, '古犹登第厚颜，安知今之必异', '鸟尚衔枝封陇，可以人而不如', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (653, '天意起斯文，不是一封书，安得先生到此', '人心归正道，只须八个月，至今百世师之', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (654, '依然锡麓书堂，南渡文章，上跨萧杨范陆', '允矣龟山道脉，东林弦诵，同源濂洛关闽', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (655, '文宣聿启文昭，木铎千秋，教著江河日月', '有虞肇开有宋，云仍万祀，道垂礼乐诗书', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (656, '疏许立身，一饭心常悬北阙', '功存讲学，半弓地已辟东林', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (658, '德泽沛三吴，百利俱兴，万姓讴歌如昨', '勋名崇两代，四贤济美，千秋俎豆常新', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (659, '望重三朝持亮节', '书成十事秉丹心', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (660, '文克经邦，武克定乱，勋名过开元宰相', '忠以辅主，哲以保身，理学推大宋名儒', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (661, '立朝与天子宰相争是非，悉宗社远谋，国本重计', '居恒共师弟朋友相讲习，惟至善性体，小心工夫', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (662, '教成于家，溯三国六朝，光昭世德', '慎追乎远，本一门双义，佑启后人', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (663, '事君以道，无惮杀身，昔人称锡谷四谏之冠', '当官而行，不求利己，后世高碧山十老之风', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (664, '牧民资保障，自公至始筑城垣，捍患御灾，当首数蓉湖名宦', '守土展明禋，况我来忝同籍里，报功崇德，只心仪枌社先贤', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (665, '遗爱成神，比诸南国甘棠，苹藻洁蠲民罔替', '降康祈福，荐以西湖秋菊，梓桑恭敬我尤宜', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (666, '六经万户千门，只慎独两言，上接泗滨，下肩伊洛', '三疏九年再窜，痛引裾一袂，晓行岭海，夜渡潇湘', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (667, '读生前浩气之歌，废书而叹', '结再世孤忠之局，过墓兴哀', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (668, '心痛鼎湖龙，一寸江山双血泪', '魂归华表鹤，二分明月万梅花', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (669, '殉社稷，只江北孤城，剩水残山，尚留得风中劲草', '葬衣冠，有淮南抔土，冰心铁骨，好伴取岭上梅花', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (670, '一代兴亡关气数', '千秋庙貌傍江山', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (671, '七十日带发效忠，表太祖十六朝人物', '三千人同心赴义，存大明一百里江山', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (672, '青山有幸埋忠骨', '白铁无辜铸佞臣', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (674, '丹陛披肝，千古纲常可托', '荒庭屈膝，两人富贵何为', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (675, '旧事总惊心，阶前桧贼', '感时应溅泪，庙侧花神', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (676, '千秋冤狱莫须有', '百战忠魂归去来', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (677, '身窜冷山，万死竟回苏武节', '魂依葛岭，千秋长傍鄂王坟', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (678, '八百里湖山，知是何年图画', '十万家烟火，尽归此处楼台', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (679, '长与流芳，一片当年乾净土', '宛然浮玉，千秋此处妙高台', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (680, '孤屿有邻，喜得卓公称后死', '严陵在望，直呼皋父哭先生', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (681, '久要不忘平生之言，古谊若龟鉴，忠肝若铁石', '敢问何为浩然之气，镇地为河岳，丽天为日星', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (683, '祠接谢亭，亦有文章惊海甸', '忠符信国，并悬肝胆照江心', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (684, '赤手挽银河，公自大名垂宇宙', '青山埋白骨，我来何处吊英贤', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (685, '千古痛钱塘，并楚国孤臣，白马江边，怒卷千堆雪浪', '两朝冤少保，同岳家父子，夕阳亭里，心伤两地风波', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (686, '赖社稷之灵，国有君矣', '竭股肱之力，死以继之', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (687, '开绝学于胡叔心陈公甫王阳明之前，享祀方堪从庙庑', '集大成于西河氏太史公文中子之后，诞灵应不愧河津', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (688, '待张巡若同胞，先死后死，与常山平原义分一席', '恨李翰不作传，大书特书，赖紫阳涑水笔补千秋', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (689, '非关貌取前人，有德有言，千载风徽追石室', '但觉神传阿堵，亦模亦范，四时俎豆式金阊', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (690, '百代集冠裳，烁古炳今，总不外纲常名教', '三吴崇俎豆，维风励俗，岂徒在科第文章', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (691, '唐代论诗人，李杜以还，惟有几篇新乐府', '苏州怀刺史，湖山之曲，尚留三亩旧祠堂', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (692, '唐史传偏遗，合循吏儒林，读书不碍中年晚', '苏州官似谥，本清才名德，卧理能教末俗移', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (693, '讽谕岂无因，乐府正声熟人口', '行藏何足辨，名山大业定生前', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (694, '姓字播弦歌，韦白以来成别调', '功名起刀笔，萧曹自古是奇才', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (695, '洛蜀任分门，惟楚有才，增赋肯凭官似虎', '河淮方夺路，如尊乃勇，拯民忍使国无鸠', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (696, '兵甲富于胸中，一代功名高宋室', '忧乐关乎天下，千秋俎豆重苏台', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (697, '盛典继毗陵，表千秋潜德幽光，长使冰心昭炜管', '新祠崇惠麓，聚两邑贞姬淑媛，群钦风节树香苏', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (698, '到此日方辨妍媸，更向鸿蒙开面目', '过这关才算儿女，还从祖父种根苗', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (699, '宝痘匀圆，喜个个金丹换骨', '天花消散，愿家家玉树成林', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (700, '当蜀吴魏之交，扰攘一时能择主', '附刘关张而后，偏裨千古竟传名', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (701, '君身自是有仙骨', '我犹未免为乡人', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (702, '微斯人吴其为沼', '赖此老海不扬波', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (703, '远看疑画，近看似诗，及至身到其间，又觉诗画都无著手处', '善者敬神，恶者畏鬼，究竟皆非异物，须知神鬼出在自心头', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (704, '神为德其盛乎，呼吸回天登彼岸', '臣何力之有也，忠诚若水证平生', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (705, '大海茫茫，到无岸无边，观于天，天高在上', '飘风发发，正可危可惧，徯我后，后来其苏', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (706, '百行孝为先，论心不论事，论事贫家无孝子', '万恶淫为首，论事不论心，论心终古少完人', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (707, '修到神仙，看三醉飞来，也要几杯绿酒', '托生人世，算百般好处，都成一枕黄粱', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (708, '西方贝叶演真经，总不出戒定慧三条法律', '南海莲花生妙相，也只消闻思修一味圆通', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (709, '色相出真空，眼界光明，震旦云霞围舍利', '声闻归妙有，耳根清净，乾陀钟鼓应迦陵', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (710, '一心念佛佛如来，鹤唳猿啼，都演出三生妙谛', '千手示人人不悟，龟毛兔角，直指开四大疑团', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (711, '佛言三藐三菩提，礼此庄严，即可承三生度脱', '人贵一喷一醒豁，除他挂碍，何难证一味圆通', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (712, '一朝弃甲成仙，不用偏裨传两晋', '万劫衔杯乐圣，聊将散汉著三唐', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (713, '蕴玉函珠，善贾固皆蒙乐利', '心耕笔织，寒儒亦可荐馨香', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (714, '义胆大于身，陷阵摧锋，在昔号常山虎将', '忠魂符厥号，兴云降雨，至今冠洮水龙神', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (715, '改草衣卉服之观，人间温暖', '极错采镂金之妙，天下文明', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (716, '元龙币聘以来，泽被广陵，到此日青囊未烬', '孟德头颅安在，烟消漳水，让先生碧血常新', '', '', '2025-03-18 06:11:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (717, '登此山一半，已是壶天', '造绝顶千重，尚多福地', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (718, '雨不崇朝遍天下', '花随流水到人间', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (719, '揽月居然凌上界', '搴云便要洒齐州', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (720, '近四旁惟中央，统泰华恒衡，四塞关河拱神岳', '历九朝为都会，包伊洛瀍涧，三台风雨作高山', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (721, '峻岳镇幽燕，近翊黄图，风雨永昭和会', '灵山锺毕昴，遥连紫塞，阴阳迭起贞元', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (722, '三峰三霄通，宝掌千秋留藓迹', '一岳一石作，金天万里矗莲花', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (723, '鸳瓦帖云霄，俯挹明星兼玉女', '虎贲卧庭庑，犹强周柏与秦松', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (724, '居艮位而践离躔，溥雷池风穴之功，柱镇天南，斗横地北', '列三公而配四岳，标月馆露台之胜，帆随湘转，雁到峰回', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (725, '望望七十二峰，工部游时，诗圣有谁能继响', '遥遥一千余载，文公去后，岳云从此不轻开', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (726, '慧眼光中，开半亩红莲碧沼', '烟花象外，坐一堂白月清风', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (727, '窗前绿树分禅榻', '城外青山到酒杯', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (728, '一庭芳草围新绿', '十亩藤花落古香', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (729, '万菊充庭秋富贵', '双藤蔓地古烟霞', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (730, '十七夕彩满蟾宫，赓隔夜霓裳旧曲', '廿五载班联鹓序，萃当年蓉镜群仙', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (731, '寿宇覃禧，借缑山鹤舞余筹，更谱瑶笙谐凤吹', '晚香励节，集蓬岛鹓班旧侣，重翻霓羽侑鸾觞', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (732, '旁人错比扬雄宅', '异代应教庾信居', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (733, '地近春明，忆当年甥馆清娱，几听后堂丝竹', '序先秋禊，幸此日师门暇豫，共陪高阁襜帷', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (734, '匝地清阴三伏候', '参天老树百年余', '', '', '2025-03-18 06:11:19');
