INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (737, '高处不胜寒，溯沙鸟风帆，七十二沽丁字水', '夕阳无限好，对燕云蓟树，百千万叠米家山', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (738, '月上斗圆光，示教禅心兼法味', '风吹清梵乐，归诚景福应真言', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (739, '为定慧，为声闻，布金地于祗园，六通朗彻', '或净名，或缘觉，转法轮于鹿苑，五蕴圆明', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (740, '云笼夜月原无碍', '鸟宿秋林亦放参', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (741, '下笔千言，正桂子香时，槐花黄后', '出门一笑，看西湖月满，东浙潮来', '', '', '2025-03-18 06:11:19');
