INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (746, '欲共水仙荐秋菊', '长留学士住西湖', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (747, '泥上偶然留指爪', '故乡无此好湖山', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (748, '但是人家有遗爱', '曾将诗句结风流', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (749, '两庑荐馨香，咸钦名相谟猷，大儒学问', '六桥揽风月，犹似川云宦迹，烟雨家乡', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (750, '祠傍水仙王，北宋尚留高士躅', '树成香雪海，西湖重见古时春', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (751, '华表千年，遗蜕可闻元鹤语', '孤山一角，暗香先返玉梅魂', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (752, '身比闲云，月影溪光堪证性', '心同流水，松声竹色共忘机', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (753, '露气春林，月华秋水', '晴光淑景，芳草远山', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (754, '四山滴翠环初地', '一路听泉到上方', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (755, '以众华为物事', '作双树之道场', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (756, '身似菩提心似镜', '云在青天水在瓶', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (757, '山外皆山，峦岫绕成清净界', '画中有画，笙歌谱就太平图', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (758, '圣德遐昌，北极恩光昭北阙', '皇仁远被，西朝瑞霭接西天', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (759, '薄宦寄明湖，有梦难寻荆树影', '前因迷法雨，招魂空叩木樨禅', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (760, '著作集名流，好事斅当年白傅', '文章留慧业，赏音俟后世扬云', '', '', '2025-03-18 06:11:19');
