INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (762, '惟唐代二幢，是峨嵋山人未过前屋', '此壁上三律，乃空翠亭僧初梦时诗', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (763, '似洞非洞，适成仙洞', '无门有门，是为佛门', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (764, '功在生民，惜传闻异辞，信史尚留曲笔', '德垂弈祀，怅播迁中叶，支流莫溯真源', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (766, '翠翠红红，处处莺莺燕燕', '风风雨雨，年年暮暮朝朝', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (767, '愿天下有情的，都成了眷属', '是前生注定事，莫错过姻缘', '', '', '2025-03-18 06:11:19');
