INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (769, '月似丹光出高岭', '鹤因梅树住前山', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (770, '四万青钱，明月清风今有价', '一双白璧，诗人名将古无俦', '', '', '2025-03-18 06:11:19');
