INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (773, '千树桃花万年药', '半潭秋水一房山', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (774, '一百八记钟声，唤起万家春梦', '二十四番风信，吹香七里山塘', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (775, '吃惠州饭，和渊明诗，陶云吾云，书就一篇归去好', '判维摩凭，到东坡界，人相我相，笑看二土往来同', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (776, '岭海答传书，七百年佛地因缘，不仅高楼邻白傅', '岷峨回远梦，四千里仙踪游戏，尚留名刹配黄州', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (777, '翰墨溯高风，轮扶大雅', '椒馨荐遗爱，鼎峙前修', '', '', '2025-03-18 06:11:19');
