INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (779, '一序证前游，太白光芒神久在', '三章怀绝调，牡丹时节我刚来', '', '', '2025-03-18 06:11:19');
