INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (795, '诗酒神仙，天自梦中传彩笔', '楼台花月，人从江上拜宫袍', '', '', '2025-03-18 06:11:19');
