INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (801, '此地有崇山峻岭，茂林修竹', '是能读三坟五典，八索九丘', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (802, '放鹤去寻三岛客', '任人来看四时花', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (803, '胜地怕重经，记当年丝竹宴诸生，回头似梦', '名园须得主，幸此日楼台逢哲匠，著手成春', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (804, '太守风清，江左依然迎谢傅', '先生来晚，山中久已卧袁安', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (805, '守郡继先人，看江水长流，剩几个当年父老', '梓薪绵世泽，愿黄堂少住，留一枝此日甘棠', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (806, '上客尽知名，杜牧诗才，鲍昭赋手', '前贤有遗韵，魏公芍药，永叔荷花', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (807, '大江东去，浪淘尽千古英雄，问楼外青山，山外白云，何处是唐宫汉阙', '小苑春回，莺唤起一庭佳丽，看池边绿树，树边红雨，此间有舜日尧天', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (808, '民不能忘，始信渊云兼政事', '敏而好学，莫疑巢许是闲人', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (809, '廉吏可为，鲁山四面墙垣少', '达人知足，陶令归来岁月多', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (810, '著手成春，卷中著述皆千古', '有官不仕，林下逍遥见一人', '', '', '2025-03-18 06:11:19');
