INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (844, '樽前帆影，槛外岚光，数胜迹重重，都向江头开画本', '楼上仙人，阁中帝子，溯游踪历历，又来亭畔吊忠魂', '', '', '2025-03-18 06:11:19');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (845, '跨太白楼之上，鸳瓦排云，倚画槛一味乡愁，已渐近钟阜晴岚，六朝城郭', '横彭蠡江而西，鹭涛堆雪，唤沙鸥共谈宦迹，最难忘峨眉春水，万里风帆', '', '', '2025-03-18 06:11:19');
