INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1087, '岱色苍茫众山小', '天容惨淡大星沉', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1088, '大名诸葛身先死', '异姓汾阳帝不疑', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1089, '汾阳王名位相同，功业常新，万里有将军壁垒', '忠武侯经纶未尽，英灵如在，百蛮拜远相祠堂', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1090, '心怆老门生，执馈缝衣怜雪立', '恩醲贤宰相，醊卮经被报春晖', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1091, '富春江万古青山，阡表长留，慈训能成贤宰相', '听雨堂九年绛帐，食单亲检，旧恩最感老门生', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1092, '包罗海岳之才，久矣韩文能立制', '绘画乾坤之手，惜哉尧典未终篇', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1093, '文星酒星书星，在天不灭', '金管银管斑管，其人可传', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1094, '读书经世即真儒，遑问他一席名山，千秋竹简', '学佛成仙皆幻相，终输我五湖明月，万树梅花', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1095, '使节转闽疆，旧雨追思，鄂渚兰言如昨梦', '耆英推洛社，生刍遥奠，谢庭玉树总层霄', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1096, '天北掩台垣，闻说槐音中夜断', '江东失宗衮，心伤荆树一时摧', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1097, '朝无谏草，家有赐书，卅载清声光简册', '公应骑箕，我悲陟岵，一时血泪洒葭莩', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1098, '帝畀以河，三策勤劳著淮北', '臣心似水，四知风节媲关西', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1099, '名在千秋，服郑说经刘杜史', '神归一夕，仙人骨相宰官身', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1100, '青宫授几，洛社图形，官府神仙皆慧业', '达尊备三，绝艺擅四，儒林文苑并传人', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1101, '四十年生有自来，身到蓬瀛天遽召', '三千里没而犹视，心伤桑梓母何依', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1102, '孝思尽宦海家园，荣亲养亲，一笑生天证佛果', '道望齐太山梁木，吾仰吾放，几人入座哭春风', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1103, '万里儿啼，此日愁攀贤令辙', '卅年老泪，隔江空盼少微星', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1104, '一品承恩，魂魄长依华屋', '九重念旧，馨香宜彻幽泉', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1105, '画里传衣，夙契偶同永长老', '山中献盖，前尘谁证衲禅师', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1106, '竹萎蕉枯，此日是师真面目', '焚香洒水，当年惟我旧朋俦', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1107, '临去诗成，写照髯仙，明月清风人已远', '平生墨妙，瓣香冰叟，虹楼瀛海世争传', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1108, '大云暂作闲云，风规自在', '元夜顿成长夜，月魄同亏', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1109, '撒手了无难，夜宴方阑归碧落', '伤心将大用，夕阳虽好近黄昏', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1110, '天道何知，不许阿奶留李贺', '神仙安在，翻教老泪哭羊昙', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1111, '一百年弹指光阴，天胡靳此', '九十载齐眉夫妇，我独何堪', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1112, '朵殿奉丝纶，四百纸述事记言，史馆犹传大手笔', '明湖思俎豆，九十载清风俭德，邦人长想古衣冠', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1113, '功名事业文章，他生末卜', '嬉笑悲歌怒骂，到此皆休', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1114, '誉美西清，望重西台，又威肃西曹，出治懋勋猷，两省春风思太守', '耗传东浙，心伤东鲁，奈身羇东粤，招魂长叹息，一江秋水哭先生', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1115, '十四日病莫能兴，幸乔梓相依，屡纩尚能亲含玉', '三千里没而犹视，痛桑榆垂暮，倚闾犹自盼泥金', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1116, '刚毅木讷近仁，生原无忝', '聪明正直而一，没则为神', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1117, '月白风清其有意', '斗量车载已无名', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1118, '生来富贵人家，却怪怪奇奇，只落得终身贫贱', '赖有聪明根器，愿生生世世，莫造此各种因缘', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1120, '地接西清，最难忘枢密院旁，公余茶话', '恩深南徼，惜空留昆明池畔，去后棠阴', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1121, '匪特茑萝亲，髫同笔研相将老', '空怀松菊志，家少田园竟不归', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1122, '与人何尤，可怜白发双亲，养子聪明成不幸', '自古有死，太息青云一瞬，如君摇落更堪悲', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1123, '德合荀君，久以范模孚梓里', '文追白傅，岂惟政事在杭州', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1124, '三管失斯人，癸水辰山都暗淡', '千秋存定论，乡贤名宦伫馨香', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1125, '旷典迈千秋，带砺台衡，天锡殊纶荣卫霍', '仁恩周十省，韬钤黼黻，人从华屋仰皋夔', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1126, '学究天人，只曾博文章一第', '才堪公辅，仅试官丞倅三年', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1127, '道学风高，望推南诏', '循良绩著，念切东人', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1128, '百金囊尽扬州死', '万里魂归蜀道难', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1129, '缘尽先离，伤心卌载荆枝，漫说来生还有约', '事多未了，回首七旬萱荫，敢言已死便无知', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1130, '仗我佛慈悲指示，方悟得无我无人', '做吾儒切实工夫，巴结到而今而后', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1131, '民是贾儿，甘棠歌众母母', '官真佛子，妙莲现法身身', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1132, '近承十五叶囊箱，阐发金书玉字', '遥印千乘侯矩矱，优游圣域贤关', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1133, '十五传兰玉相承，授砚三看入芸馆', '六千里泥金甫报，抚楹一笑返蓉城', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1134, '易箦预知时，一日期先曹太傅', '盖棺先定论，千秋名并阿文成', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1135, '出将入相垂五十年，功比汾阳，寿同潞国', '扫穴犁庭越三万里，昔追定远，今媲章佳', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1136, '帝眷老成人，直追溯昭代前贤，当之克配', '师承先大父，从此问昔年遗事，知者其谁', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1137, '名父名翁名女士', '大贞大孝大完人', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1138, '济艰辛，尝险阻，贫家妇信难为，痛今朝镜破钗分，欲图梦影重圆，除异世再同青玉案', '习荆布，厌绮罗，半生俭应可法，奈尘海飙驰电掣，赢得褶痕如旧，到秋宵怕检缕金箱', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1139, '四千里累尔远来，父在家，母在殡，翁姑在堂，属纩定知难瞑目', '廿三年弃余永诀，拜无儿，哭无女，继承无侄，盖棺未免太伤心', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1140, '仙去何之，烧鼎白云栖断壑', '神伤已甚，著书黄叶冷空山', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1141, '千佛礼鸠摩，名士案头赓昧旦', '五更惊蜕羽，天香馆畔咽秋风', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1142, '蟾镜掩清辉，叹当年玉宇琼楼，难觅灵丸延寿药', '鹿车随大隐，知此后故奁遗挂，重哦寒夜悼亡诗', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1143, '家有诗仙，惜到处名山，未能偕隐', '身常礼佛，觉往生净域，确有明征', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1144, '奔月访姮娥，忍令天香虚旧馆', '持花归净土，徒烦松雪礼中峰', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1145, '仙娥明月是前身，想归真翠水丹林，桂蕊灵香同郁烈', '名士秋风添别恨，漫寄意绳床经案，虫丝落叶共凄清', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1146, '解脱拈花刚佛日', '证明因果在仙霞', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1147, '淑德称女师，孝著郑经，礼修梁案', '令名相夫子，泽流三党，絮被万家', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1148, '相夫垂四十载辛勤，出处同心，昼锦归来犹并辔', '济世具万千缗功德，炽昌启后，夜台化去合生天', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1149, '千里蒪羹，方偕归隐，三间茆屋，幸免赁舂，卅八载鸿案相庄，痛半世钗荆，彤史遽亡贤德曜', '鉴鞶才思，葭末心钦，书带家声，兰猷耳熟，廿六年鲤庭陪侍，对后堂丝竹，绛帷望断老彭宣', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1150, '天下大事公可属', '江南遗爱民不忘', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1151, '尹文端厚泽深仁，重见江乡说遗爱', '陈恪勤精心果力，方知楚产信多才', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1152, '师范重三天，謦欬声犹留殿陛', '皇华周四国，朴诚望早式班联', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1153, '仪表称科名，帝许云程难限量', '文章兼政事，我惭风义托渊源', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1154, '春江桃叶莺啼湿', '夜雨梅花蝶梦寒', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1155, '宝瑟无声弦柱绝', '瑶台有月镜奁空', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1156, '穗帷忍痛安仁句', '椎髺难忘德曜风', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1157, '气数不言仁者寿', '性情犹见古之愚', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1158, '诔文作自先生妇', '遗稿归于后死朋', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1159, '兰亭少长悲陈迹', '玉局风光叹化身', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1160, '素车有客奔元伯', '绝调无人继广陵', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1161, '云深竹径樽犹在', '雪压芝田梦不回', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1162, '文章卓荦生无敌', '风骨精灵没有神', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1163, '事业已归前辈录', '典型留与后人看', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1164, '称觞尚忆登堂事', '挂剑难为过墓情', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1165, '十载名场成劲敌', '九重泉路尽交期', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1166, '岘山碑堕羊公泪', '浚县图留陆子型', '', '', '2025-03-18 06:11:37');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1167, '上界由来足官府', '西风何处哭文章', '', '', '2025-03-18 06:11:37');
