INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1528, '星云照耀辉天禄', '山海崇深荟地灵', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1529, '仿天一藏书，邠馆冈原扬大烈', '应奎三悬象，沛宫父老庆同文', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1530, '天作泝前光，玉券十华征锡极', '人文观大备，瑶签四照见逢原', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1531, '益地图开，岳贡川珍胪宝笈', '丽天文焕，豳京邰室溯仙源', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1532, '从朔贻规，三院图书有典', '指南锡极，九霄云汉为章', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1533, '日映东隅，被光华于艺圃', '源流北海，挹沆瀁之文澜', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1534, '北镇标云，词林宗斗极', '东瀛浴日，学海跂壶天', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1535, '群玉成山，思文衍邰室', '上奎炳耀，绳武播邠风', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1536, '南斗焕珠弧，六旬御宇，万祀颐和，尧封祝曰圣人寿', '前星临黼扆，九陛崇基，三元肇祚，周雅歌之君子宁', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1537, '北极拱皆朝，子帝有帝，曾孙有孙，五福堂前欢舞彩', '后天锡难老，长春如春，元夜不夜，九华灯下寿称觥', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1538, '东作穑关心，雪融陇麦，烛照田蚕，课雨占晴尚初志', '左旋杓向角，节宴尧浆，灯词舜轸，抚时行庆有前规', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1539, '西域被流沙，年班藩部，岁报屯田，照世杯圆里二万', '右垣通阁道，出震迪光，乘乾垂裕，和时烛朗界三千', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1540, '东国旧戎衣，吹邠有籥，作岐有鼛，小物克勤咏糠烛', '南邦昔车斗，恬海如鳞，翕河如镜，民风可观戒灯船', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1541, '西园翰墨林，四库积玉，七阁抽琅，太乙藜光灯以右', '南宫礼乐地，两举制科，六开恩榜，文昌珠彩月同圆', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1542, '东揖木公朝，十年庆典，千叟恩荣，洛社画图鸠杖集', '北迎元日诏，五代齿繁，百家算倍，康衢灯火筿骖游', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1543, '西定噶喇依，恭者我仆，偝者我俘，蛮目更番入春宴', '北逾额济勒，威曰归降，德曰归顺，鸿臣来贺御东朝', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1544, '南面久仔肩，求衣问夜，秉烛待章，福用敷之次五极', '前盟果如意，鸟篆镌琼，鸿文刻玉，古惟稀矣四三皇', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1545, '北戒拱论都，河得真源，淮得真源，九如咸颂川方至', '后车勤觐岳，岱犹望幸，嵩犹望幸，万寿宜歌山有台', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1546, '东箱启儒席，论敷奥旨，雅肄古歌，诗诵寿万千无量', '左海侑宾筵，乐补吹笙，乡观饮酒，礼成月三五而盈', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1547, '西叙溯成功，振以特磬，声以镈钟，节序新词卑火树', '右文鬯鸣盛，风有干城，雅有髦土，科名旧事压灯球', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1548, '东震旦最尊，楼涌万佛，经译三乘，欢喜园中法轮转', '南赡部妙胜，印写秘文，塔飘吉帛，光明天上慧灯悬', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1549, '西社赛春灯，市流帑镪，釜溢仓粮，六十年逢年布惠', '南荣曝朝日，户弛镠租，漕除玉粒，二十万累万全蠲', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1550, '东陆鸟司开，辛析绀殿，亥藉黛辕，惕若躬亲弈叶守', '北辰象布令，秋狝上兰，冬嬉太液，昭哉心法髦期勤', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1551, '西来福德，智高六帝，帝享万年年，典盛礼隆胪旧政', '北向子臣，民奉至尊，尊为众父父，天符人瑞遂初心', '', '', '2025-03-18 06:12:02');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1553, '且将酩酊酬佳节', '未有涓涘答圣朝', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1554, '圣朝无弃物', '余事作诗人', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1555, '乙近杏花袍曳紫', '未匀柳色绶拖黄', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1556, '丁岁观光惭国士', '酉山探秘识奇书', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1557, '有月即登台，无论春秋冬夏', '是风皆入座，不分南北东西', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1558, '西域谈经，心仰尼山思窃比', '东周问礼，语传柱史戒深藏', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1559, '宝树蔚彤云，知诸出有阳元，望他日成吾宅相', '华堂盈紫气，溯原初于聃耳，喜长生本自家传', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1560, '前辈典型，秀才风味', '华嵩品格，河海文章', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1561, '沽酒近交乡父老', '解衣平揖汉公卿', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1562, '第五之名齐票骑', '十三此夜订心交', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1563, '奉母孝经看在手', '教儿文选读从头', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1564, '吴兴山水，古来清远', '包咸论语，今尚流传', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1565, '三馆六曹十七科，竞称前辈', '一官万里二千石，遂老斯人', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1566, '二分山色三分水', '五斗功名八斗才', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1567, '钻燧木先春，食德饮和，且自披星朝赤帝', '观灯天不夜，衢歌巷舞，何妨捧日待黄人', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1568, '度一切众生于梦幻后', '存千秋大义在天壤间', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1569, '当时诸葛大名，荒祠古柏，嗟回首万牛，何如汉寿亭垂，遍开兰若', '此地洞天小有，绝壁修篁，肃临风千纛，休比武陵源藐，空说桃花', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1570, '老屋将倾，只管淹留何日去', '新居未卜，不妨小住几时来', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1571, '六宫粉黛无颜色', '万国衣冠拜冕旒', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1572, '门多桃李，案少簿书，别宦恐无此乐', '前列生徒，后盈丝竹，今时复有其人', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1573, '元旦即称觞，鹤算龟龄齐让早', '岁朝先试乐，莺歌燕语尽翻新', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1574, '二柳当门，家计逊陶潜之半', '双桃钥户，人谋虑方朔之三', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1575, '休萦俗事催霜鬓', '且制新歌付雪儿', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1576, '孙楚楼边觞月地', '孝侯台畔读书人', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1578, '未闻安石弃东山，公能不有斯园，贤于古人远矣', '漫说少陵开广厦，彼仅空怀此愿，较之今日何如', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1579, '谏垣弥久望弥尊，看此际三公，都是当年等辈', '封事愈多功愈懋，卜将来一擢，尽补往日淹留', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1580, '世间桃李尽出公门，何须腊尽始芳菲，满眼无非春色', '天下鱼龙都归学海，不待时来方变化，启口即是雷声', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1581, '镜里有梅新晋马', '釜中无药旧唐鸡', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1582, '无法向人说', '将心替汝安', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1583, '大护法不见僧过', '善知识能调物情', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1584, '一阴一阳之谓道', '此时此际难为情', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1585, '做戏何如看戏乐', '下场更比上场难', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1586, '天上何曾有山水', '人间乐得做神仙', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1587, '辛勤有此庐，抽身归矣，喜鸟啼花笑，三径常开，好领取竹簟清风，茅檐暖日', '萧闲无个事，闭户恬然，对茶熟香温，一编独抱，最难忘别来旧雨，经过名山', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1588, '圣代即今多雨露', '故乡无此好湖山', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1589, '家传渤海箕裘远', '春到方壶雨露新', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1590, '酒阑兴倦，事往情迁，只不忘游过名山，别来旧雨', '春去仍归，人老难复，更休诧殿前起草，海外题诗', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1591, '书似青山常乱叠', '灯如红豆最相思', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1592, '过也如日月之食焉', '复其见天地之心乎', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1593, '逝者如斯夫', '掩之诚是也', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1594, '满眼蓬蒿游子泪', '一盂麦饭故乡情', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1595, '虽然毫末技艺', '却是顶上工夫', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1596, '不教白发催人者', '更喜春风满面生', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1597, '到来尽是弹冠客', '此去应无搔首人', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1598, '其交以道，其接以礼', '同声相应，同气相求', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1599, '四大皆空，坐片刻无分尔我', '两头是路，吃一盏各自东西', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1600, '欲把西湖比西子', '从来佳茗似佳人', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1601, '常如作客，何问康宁，但使囊有余钱，瓮有余酿，釜有余粮，取数叶赏心旧纸，放浪吟哦，兴要阔，皮要顽，五官灵动胜千官，过到六旬犹少', '定欲成仙，空生烦恼，只令耳无俗声，眼无俗物，胸无俗事，将几枝随意新花，纵横穿插，睡得迟，起得早，一日清闲似两日，算来百岁已多', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1602, '丈室花同天女散', '围摩诗共老人参', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1603, '以其所有，易其所无，四境之内，万物皆备于我', '或曰取之，或曰无取，三年无改，一介不以与人', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1604, '渌水漾丁帘，增我辈闲中风致', '名园依丙舍，祝君家看到云仍', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1605, '上相教除名士气', '至尊亲许读书人', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1606, '醉中掷笔金銮殿', '睡起鸣笳铁瓮城', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1607, '南中喜得秦淮海', '天下愿识韩荆州', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1608, '名辈出枢垣，本杜断房谋，陶甄南国', '鸿才领词坫，有欧书韩句，濡染东吴', '', '', '2025-03-18 06:12:14');
INSERT INTO `couplets` (`id`, `first`, `second`, `author`, `dynasty`, `created`) VALUES (1609, '霖雨东兴，枢密上才开远略', '好风西笑，湖山秀句带边声', '', '', '2025-03-18 06:12:14');
